'use strict';
var utility = require('app/utility');

function computeWhere(attr, urlParams) {
  var where = {};
  for (var key in urlParams) {
    if (urlParams.hasOwnProperty(key)) {
      if (attr.indexOf(key) !== -1) {
        where[key] = urlParams[key];
      }
    }
  }
  return utility.isEmptyObject(where) ? {} :  {where: where};
}

module.exports = {
  computeWhere: computeWhere
}